import { useState } from "react"
import Square from "./Square"

const Board = () => {
	const [square, setSquare] = useState({
		squares: Array(9).fill(null),
		xIsNext: true
	})
	const clickHandler = (index) => {
		const sq = square.squares.slice();

		sq[index] = square.xIsNext ? 'X' : 'O';
		setSquare({
			squares: sq,
			xIsNext: !square.xIsNext
		})
	}

	const winner = culculateWinner(square.squares)
	let status;
	if(winner){
		status = `Выиграл ${winner}`
	}else{
		status = `Ход игрока: ${square.xIsNext ? 'X' : 'O'}`
	}

	return (
		<div className="board">
			<div className="status">{status}</div>
			<div className="board-row">
				<Square value={square.squares[0]} clickHandler={clickHandler} index={0}/>
				<Square value={square.squares[1]} clickHandler={clickHandler} index={1}/>
				<Square value={square.squares[2]} clickHandler={clickHandler} index={2}/>
			</div>
			<div className="board-row">
				<Square value={square.squares[3]} clickHandler={clickHandler} index={3}/>
				<Square value={square.squares[4]} clickHandler={clickHandler} index={4}/>
				<Square value={square.squares[5]} clickHandler={clickHandler} index={5}/>
			</div>
			<div className="board-row">
				<Square value={square.squares[6]} clickHandler={clickHandler} index={6}/>
				<Square value={square.squares[7]} clickHandler={clickHandler} index={7}/>
				<Square value={square.squares[8]} clickHandler={clickHandler} index={8}/>
			</div>
		</div>
	)
}

export default Board
